@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun JobDetailScreen(
    viewModel: JobDetailViewModel,
    jobId: Int,
    onNavigateBack: () -> Unit
){
    val context = LocalContext.current

    val scrollState = rememberScrollState()
    val job = remember { viewModel.job }

    viewModel.getJobById(jobId){ errorMessage ->
        Toast.makeText(context, errorMessage, Toast.LENGTH_SHORT).show()
    }

    Scaffold(
        topBar = {
            Surface(
                shadowElevation = 20.dp
            ) {
                TopAppBar(
                    title = {
                        Text(
                            text = job.value.title,
                            fontWeight = FontWeight.Bold
                        )
                    },
                    navigationIcon = {
                        IconButton(onClick = {
                            onNavigateBack()
                        }) {
                            Icon(
                                imageVector = Icons.Rounded.ArrowBack,
                                contentDescription = "Navigate back"
                            )
                        }
                    }
                )
            }
        },
        content = {
            Column(modifier = Modifier
                .fillMaxSize()
                .padding(top = it.calculateTopPadding())
                .scrollable(scrollState, orientation = Orientation.Vertical)
            ) {

                DataTextbox(
                    text = job.value.description,
                    icon = Icons.Rounded.Description,
                    description = "Job description",
                )

                DataTextbox(
                    text = job.value.pin,
                    icon = Icons.Rounded.Key,
                    description = "Connection PIN"
                )

                Column(
                    horizontalAlignment = Alignment.CenterHorizontally,
                    modifier = Modifier
                        .padding(10.dp)
                        .background(Color(211, 243, 107), RoundedCornerShape(10.dp))
                        .border(3.dp, Color.Black, RoundedCornerShape(10.dp))
                        .padding(10.dp)
                ) {

                    if(job.value.owner.profilePicture != null) {
                        Image(
                            bitmap = job.value.owner.getProfileBitmap(),
                            contentDescription = "Profile picture",
                            modifier = Modifier
                                .size(100.dp)
                                .clip(RoundedCornerShape(50.dp))
                                .border(3.dp, Color.Black, RoundedCornerShape(50.dp))
                        )
                    }
                    else{
                        Image(
                            imageVector = Icons.Rounded.Person,
                            contentDescription = "Profile picture",
                            modifier = Modifier
                                .size(100.dp)
                                .clip(RoundedCornerShape(50.dp))
                                .background(Color.White)
                                .border(3.dp, Color.Black, RoundedCornerShape(50.dp))
                        )
                    }


                   DataTextbox(
                       text = job.value.owner.fullName,
                       icon = Icons.Rounded.Person,
                       description = "Owner full name",
                       backgroundColor = Color.White
                   )

                   DataTextbox(
                       text = job.value.owner.nickname,
                       icon = Icons.Rounded.Badge,
                       description = "Owner nickname",
                       backgroundColor = Color.White
                   )

                   DataTextbox(
                       text = job.value.owner.email,
                       icon = Icons.Rounded.Email,
                       description = "Owner email address",
                       backgroundColor = Color.White
                   )
                }
            }
        }
    )
}

@Composable
fun DataTextbox(
    text: String,
    icon: ImageVector,
    description: String,
    backgroundColor: Color = Color(211, 243, 107),
    modifier: Modifier = Modifier.padding(vertical = 5.dp, horizontal = 10.dp)
){
    Column(
        modifier = modifier
            .fillMaxWidth()
            .clip(RoundedCornerShape(10.dp))
            .background(backgroundColor)
            .border(3.dp, Color.Black, RoundedCornerShape(10.dp))
            .padding(10.dp)
    ) {
        Row(
            verticalAlignment = Alignment.CenterVertically,
            horizontalArrangement = Arrangement.Start,
            modifier = Modifier
                .fillMaxWidth()

        ) {
            Icon(imageVector = icon, contentDescription = description)

            Spacer(modifier = Modifier.width(5.dp))

            Text(
                text = description,

            )
        }

        Text(
            text = text,
            fontSize = 18.sp,
            fontWeight = FontWeight.Bold,
            modifier = Modifier
                .fillMaxWidth()
                .padding(start = 20.dp),
        )
    }

}

